using System.Collections.ObjectModel;
using System.ComponentModel;
using System.Runtime.CompilerServices;

namespace TestImages;

public partial class MainPage : ContentPage, INotifyPropertyChanged
{
	volatile bool run;
	public static string base64CatImage = "";
	public static string base64DogImage = "";
	private ObservableCollection<Animal> _animals = new ObservableCollection<Animal>();
	private Animal _selectedAnimal;
	Random random = new Random();
	public MainPage()
	{
		InitializeComponent();
		BindingContext = this;
	}
	public ObservableCollection<Animal> Animals
	{
		get => _animals;
		set => SetProperty(ref _animals, value);
	}

	public Animal SelectedAnimal
	{
		get => _selectedAnimal;
		set => SetProperty(ref _selectedAnimal, value);
	}
	//ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(BaseImage)))
	public void AddOne()
	{
		MainThread.InvokeOnMainThreadAsync(async () =>
		{
			Animals.Insert(0, new Animal(new List<ImageSource>() { ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(base64CatImage))) }, "cat"));
			Animals.Insert(0, new Animal(new List<ImageSource>() { 
				ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(base64DogImage))),
				ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(base64DogImage))),
				ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(base64DogImage)))
			}
			, "dog"));
			while (Animals.Count >100)
			{
				Animals.Remove(Animals.Last());
			}

		});
	}

	#region INotifyPropertyChanged

	public event PropertyChangedEventHandler PropertyChanged;

	private bool SetProperty<T>(ref T storage, T value, [CallerMemberName] string propertyName = null)
	{
		if (!object.Equals(storage, value))
		{
			storage = value;
			RaisePropertyChanged(propertyName);
			return true;
		}
		return false;
	}

	private void RaisePropertyChanged(string propertyname)
	{
		PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(propertyname));
	}

	#endregion

	private void ContentPage_Loaded(object sender, EventArgs e)
	{
		run = true;
		Device.StartTimer(new TimeSpan(0, 0, 1), () => {
			if (run) {
				MainThread.InvokeOnMainThreadAsync(async () =>
				{ AddOne(); });
				return true; }
			else { return false; }
		});
	}
}

public class Animal
{
	public Animal(List<ImageSource> images, string name)
	{
		Name = name;
		Images = images;
	}

	public List<ImageSource> Images { get; set; }
	public string Name { get; set; }

}


